import styles from './Product.module.scss';
import clsx from 'clsx';
import Button from '../Button/Button';
import PropTypes from 'prop-types';
import { useState } from 'react';

const Product = props => {
  const data = props.data;

  const [currentColor, setCurrentColor] = useState(data.colors[0]);
  const [currentSize, setCurrentSize] = useState(data.sizes[0].name);

  const prepareColorClassName = color => {
		return styles["color" + color[0].toUpperCase() + color.substr(1).toLowerCase()];
  }; 

  const getPrice = () => {
    return (
      data.basePrice + data.sizes.find(name => name === currentSize).additionalPrice
    );
  }
  
  return (
		<article className={styles.product}>
			<div className={styles.imageContainer}>
				<img
					className={styles.image}
					alt={data.title}
					src={`${process.env.PUBLIC_URL}/images/products/shirt-${data.name}--${currentColor}.jpg`}
				/>
			</div>
			<div>
				<header>
					<h2 className={styles.name}>{data.title}</h2>
					<span className={styles.price}>Price: {getPrice}$</span>
				</header>
				<form>
					<div className={styles.sizes}>
						<h3 className={styles.optionLabel}>Sizes</h3>
						<ul className={styles.choices}>
							{data.sizes.map((size) => (
								<li key={size.name}>
									<button
										type="button"
										className={clsx(size.name === currentSize && styles.active)}
									>
										{size.name}
									</button>
								</li>
							))}
						</ul>
					</div>
					<div className={styles.colors}>
						<h3 className={styles.optionLabel}>Colors</h3>
						<ul className={styles.choices}>
							{data.colors.map(color => (
								<li key={color}><button type="button" className={clsx(prepareColorClassName(color), props.currentColor === color && styles.active)} onClick={(e) => setCurrentColor(color)}/></li>
              ))}
						</ul>
					</div>
					<Button className={styles.button}>
						<span className="fa fa-shopping-cart" />
					</Button>
				</form>
			</div>
		</article>
	);
};

Product.propTypes = {
	name: PropTypes.string.isRequired,
	title: PropTypes.string.isRequired,
	basePrice: PropTypes.number.isRequired,
	sizes: PropTypes.array.isRequired,
	colors: PropTypes.array.isRequired,
};

export default Product;
